import React, { Component } from 'react';
import TutorCard from './TutorCard';
import axios from "axios";

class Collection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [],
      activeUsers: []
    };
  }

  componentDidMount() {
    axios
      .get("http://127.0.0.1:8000/api/users/")
      .then(res => this.setState({ users: res.data }))
      .catch(err => console.log(err));
  }

  isActiveTutor(){
    for(let i = 0; i < this.users.length; i++) {
      if (this.users[i].is_tutor && this.users[i].is_active){
        this.activeUsers[i]=this.users[i]
      }
    }
  }
  render() {
    return  (
      <div className="app">
        <div className="collection">
          <h2 className="upcoming-appointments-text">Browse Tutors</h2>

            {
              this.state.users.map((user,k) => (

              <TutorCard  key={k} user={user}
              />
            ))}
            <p className="tutor-results">{this.state.users.length} tutors found.</p>
          </div>
        </div>
      )
    }
  }

export default Collection;
